#!/bin/bash
set -e

app=$1

echo "Update Cuteness to local feed"
if [ -f $app/app/cuteness.io/reddit-app-view-model.ts ]; then
  sed -i -e "s/(1000)/(50)/g" $app/app/cuteness.io/reddit-app-view-model.ts
  sed -i -e "s/https/http/g" $app/app/cuteness.io/reddit-app-view-model.ts
  sed -i -e "s/www.reddit.com\/r/nsbuild01.telerik.com/g" $app/app/cuteness.io/reddit-app-view-model.ts
elif [ -f $app/app/cuteness.io/reddit-app-view-model.js ]; then
  sed -i -e "s/(1000)/(50)/g" $app/app/cuteness.io/reddit-app-view-model.js
  sed -i -e "s/https/http/g" $app/app/cuteness.io/reddit-app-view-model.js
  sed -i -e "s/www.reddit.com\/r/nsbuild01.telerik.com/g" $app/app/cuteness.io/reddit-app-view-model.js
fi
