#!/bin/bash
set -e

app=$1

echo "Setup Firebase ..."
cp -f ./ns-setup/ci/QSF/google-services.json $app/app/App_Resources/Android/
cp -f ./ns-setup/ci/QSF/GoogleService-Info.plist $app/app/App_Resources/iOS/
sed -i -e "s/const enabled = false;/const enabled = true;/g" $app/app/common/firebase.ts

echo "Setup build.xcconfig ..."
cp -f ./ns-setup/ci/QSF/build.xcconfig $app/app/App_Resources/iOS/
