#!/bin/bash
set -e

app=$1

echo "Copying fonts ..."
cp -R $app/app/ui-tests-app/resources/fonts $app/app/

echo "Copying images ..."
cp $app/app/ui-tests-app/resources/images/testlogo.jpg $app/app/App_Resources/Android/drawable-nodpi/
cp $app/app/ui-tests-app/resources/images/testlogo.jpg $app/app/App_Resources/iOS/
cp $app/app/ui-tests-app/resources/images/*.png $app/app/App_Resources/iOS/
