#!/bin/bash
set -e

sample=$1

if [ -f $sample/package.json ]; then
    echo "Catenate $sample/package.json file"
    cat $sample/package.json
fi

if [ -f $sample/node_modules/tns-core-modules/package.json ]; then
    echo "Catenate $sample/node_modules/tns-core-modules/package.json file"
    cat $sample/node_modules/tns-core-modules/package.json
fi

if [ -f $sample/node_modules/tns-core-modules/node_modules/tns-core-modules-widgets/package.json ]; then
    echo "Catenate $sample/node_modules/tns-core-modules/node_modules/tns-core-modules-widgets/package.json file"
    cat $sample/node_modules/tns-core-modules/node_modules/tns-core-modules-widgets/package.json
fi

if [ -f $sample/node_modules/nativescript-angular/package.json ]; then
    echo "Catenate $sample/node_modules/nativescript-angular/package.json file"
    cat $sample/node_modules/nativescript-angular/package.json
fi
