#!/bin/bash
set -e

# Do not remove !!!

if [ "$#" -eq 1 ]; then
    appDir=$1
else
    appDir=TestApp
fi

echo "Clean up all files and folders
        rm -rf $appDir/platforms &&
        rm -rf $appDir/node_modules &&
        rm -rf $appDir/hooks &&
        rm -rf ${PWD}/out &&
        rm -rfv ${PWD}/*.* &&
        mkdir ${PWD}/out
"
rm -rf *.* &&
rm -rf $appDir/platforms &&
rm -rf $appDir/node_modules &&
rm -rf $appDir/hooks &&
rm -rfv ${PWD}/out &&
mkdir ${PWD}/out
