#!/bin/bash
set -e

echo "Remove Caches"
sudo rm -fr /Library/Caches/com.apple.dt.instruments/* && true

echo "Remove Xcode derived data"
sudo rm -fr ~/Library/Developer/Xcode/DerivedData/* && true

echo "Remove Xcode build folders"
sudo find /var/folders/ -type d -name '*-Prefix-*' -exec rm -fr {} \; > /dev/null 2>&1 && true
