#!/usr/bin/env node

const path = require("path");
const fs = require("fs");

if (process.argv.length < 3) {
    throw new Error("You must specify a project path!");
}

let packageJsonPath = path.resolve(process.argv[2], "package.json");
let packageJson = require(packageJsonPath);

packageJson.scripts = packageJson.scripts || {};
packageJson.scripts["ns-verify-bundle"] = "ns-verify-bundle";
fs.writeFileSync(packageJsonPath, JSON.stringify(packageJson, null, 2));
