#!/bin/bash
set -e

app=$1
branch=$2

echo "Create JS Project"
rm -rf $app
echo "You are in $branch branch:"
if [[ "$PWD" == *hotfix* ]]; then
    if [ -z "$TEMPLATE_VERSION" ]; then
        echo "TEMPLATE_VERSION is unset"; 
        TEMPLATE_VERSION=`echo $FIX_MODULES | cut -c 1-3`           
    else 
        echo "TEMPLATE_VERSION is set to '$TEMPLATE_VERSION'"; 
    fi
    node_modules/.bin/tns create $app --template tns-template-hello-world@$TEMPLATE_VERSION
else
    node_modules/.bin/tns create $app --template https://github.com/NativeScript/template-hello-world.git#$branch
fi

