#!/bin/bash
set -e

app=$1
branch=$2

echo "Create NG Project"
rm -rf $app
echo "You are in $branch branch:"
if [[ "$PWD" == *hotfix* ]]; then
    version=`echo $FIX_MODULES | cut -c 1-3`
    node_modules/.bin/tns create $app --template tns-template-hello-world-ng@$version
else
    node_modules/.bin/tns create $app --template https://github.com/NativeScript/template-hello-world-ng.git#$branch

fi