brew install xz
curl http://releases.llvm.org/4.0.0/clang+llvm-4.0.0-x86_64-apple-darwin.tar.xz --output clang+llvm-4.0.0-x86_64-apple-darwin.tar.xz
xz -d clang+llvm-4.0.0-x86_64-apple-darwin.tar.xz
/usr/bin/tar -zxvf clang+llvm-4.0.0-x86_64-apple-darwin.tar
mv clang+llvm-4.0.0-x86_64-apple-darwin ~/Work/
rm -rf /usr/local/bin/llvm-config
ln -s ~/Work/clang+llvm-4.0.0-x86_64-apple-darwin/bin/llvm-config /usr/local/bin/llvm-config
ls -la /usr/local/bin/llvm-config
