#!/bin/bash

MAVEN_VERSION="3.3.9"
MAVEN_URL="http://apache.cbox.biz/maven/maven-3/${MAVEN_VERSION}/binaries/apache-maven-${MAVEN_VERSION}-bin.tar.gz"

clean() {
    rm -rf *.gz
    rm -rf cmake-$CMAKE_VERSION_SHORT
}

download_distro() {
    curl -o apache-maven-${MAVEN_VERSION}-bin.tar.gz "$CMAKE_URL"
}

unpack_source() {
    tar xzvf ./apache-maven-${MAVEN_VERSION}-bin.tar.gz
}

update_path() {
    echo 'export PATH=$PATH:$PWD/bin' >> ~/.bash_profile
}

if [[ "${BASH_SOURCE[0]}" == "${0}" ]] ; then
    clean && \
        download_distro && \
        unpack_source && \
        update_path
fi
