#!/bin/bash
set -e
branch="${1:-master}"

git_clone() {
    rm -fr functional-tests
    echo "Clone functional-tests.git#master:" $(date)
    git clone git@github.com:NativeScript/functional-tests.git --branch ${branch}
}

prepare_testapp() {
    cd functional-tests
    rm -rf testapp
    mkdir -p testapp

    echo "Copy testapp:" $(date)
    cp -v ../out/*.tgz ./testapp/ || true
    cp -v ../out/*.apk ./testapp/ || true
}

git_clone
prepare_testapp
