#!/bin/bash
set -e

echo "Run Android Tests: " $(date)
cd functional-tests
mvn clean test -P unittests.emu.default.api23 -e
rm -fr android-results
cp -R target/surefire-reports android-results
if (grep -q "runTests passed!" android-results/logs/log4j.log); then
    echo $'Android tests PASSed!\n'
else 
    echo $'Android tests FAILed!\n'
    echo $'\'runTests passed!\' NOT found in \'log4j.log\'!\n'
    exit 1
fi
