#!/bin/bash
set -e

echo "Run iOS Tests: " $(date)
cd functional-tests
mvn clean test -P unittests.sim.iphone7.ios100 -e
rm -fr ios-results
cp -R target/surefire-reports ios-results
if (grep -q "runTests passed!" ios-results/logs/log4j.log); then
    echo $'iOS tests PASSed!\n'
else 
    echo $'iOS tests FAILed!\n'
    echo $'\'runTests passed!\' NOT found in \'log4j.log\'!\n'
    exit 1
fi
