#!/bin/bash

pkill -9 Simulator
xcrun simctl shutdown booted
xcrun simctl erase all

sudo rm -rf /Library/Developer/CoreSimulator/Profiles/Runtimes/iOS\ 8.1.simruntime
sudo rm -rf /Library/Developer/CoreSimulator/Profiles/Runtimes/iOS\ 8.2.simruntime
sudo rm -rf /Library/Developer/CoreSimulator/Profiles/Runtimes/iOS\ 8.3.simruntime
sudo rm -rf /Library/Developer/CoreSimulator/Profiles/Runtimes/iOS\ 9.2.simruntime
sudo rm -rf /Library/Developer/CoreSimulator/Profiles/Runtimes/iOS\ 9.4.simruntime

rm -rf ~/Library/Preferences/com.apple.iphonesimulator.plist
defaults write ~/Library/Preferences/com.apple.iphonesimulator SimulatorWindowLastScale "1"
