ANGULAR_VER="2.4.3"
ROUTER_VER="3.4.3"
RXJS_VER="5.0.3"
TYPESCRIPT_VER="2.1.5"

CMD=$(cat <<EOF
sed -i .bak -e 's/@angular\/\([^"]*\)": "[^"]*"/@angular\/\1": "~$ANGULAR_VER"/'
    -e 's/"@angular\/router": "[^"]*"/"@angular\/router": "~$ROUTER_VER"/'
    -e 's/"rxjs": "[^"]*"/"rxjs": "~$RXJS_VER"/'
    -e 's/"typescript": "[^"]*"/"typescript": "$TYPESCRIPT_VER"/' ./Groceries/package.json
EOF
)

eval $CMD
